<style>
    .wrapper{
        height: 90%;
        display: flex;
        align-items: center;
        justify-content: center;
    }
    .tooltip .tooltiptext{
        font-size: .8rem
    }

    .tooltip.g-scale .tooltiptext{
        font-size: .58rem;
    }
    .skills-logos .tooltip .tooltiptext{
        top: 90%;
        right: -25%;
    }
    .link-btn{
        color: var(--primary-color);
        text-shadow: var(--text-shadow);
    }
    .link-btn svg{
        width: 25px;
        height: 25px;
        fill: var(--primary-color);
    }

    .social-links{
        display: flex;
        gap: .69em;
    }

    .skills-logos{
        list-style: none;
        margin: 0;
        padding: 0;
        display: flex;
        gap: .4em;
    }
    .skills-logos svg{
        width: 30px;
        height: 30px;
    }

    .btn{
        padding:.3em .5em;
        text-align: center;
    }

    .link-btn:hover .link-text{
        text-decoration: underline;
    }

    @keyframes shake{
        0% {transform: translate(0);}
        25% {transform: translate(-2px);}
        50% {transform: translate(5px);}
        75% {transform: translate(-3px);}
        100% {transform: translate(0);}

    }
    .cta:hover i{
        animation: shake 0.5s ease-in-out;
    }



    .hero-section{
        /* helps with shaky image slidein animation */
        display: flex;
        flex-direction: column;
        gap: 1em;

        word-spacing: normal;
        word-wrap: break-word;
        text-wrap: balance;
        font-size: 1rem;

        flex: 3;
    }
    .footer{
        display: flex;
        align-items: center;
        gap: 1em;
    }

    .canvas{
        align-content: last baseline;
        flex: 2;
        animation: slidein 1s linear;
    }


    @keyframes slidein{
        from{
            opacity: 0;
            margin-right: -10em ;
        }to{
            opacity: 1;
            margin-right: 0;
        }
    }


    .canvas img{
        width: 100%;
        border-bottom-right-radius: 40%;
    }

    @media (max-width:550px){

    .hero-body{
        text-align: center;
    }
    .hero-section{
        align-items: center;
        justify-content: center;
    }
    .skills{
        text-align: center;
    }
    .canvas{
        display: none;
    }
}
</style>